package ch04_02;

public class whiletest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i =0;
		
		while(i <= 5 ) {
			System.out.println("pass");
			i++;//i=i+1
		}
		
		int kor = 80;
		
		do {
			System.out.println("코리아 화이팅");
			
		} while(kor >= 100);
		
			System.out.println("최고야");
	// do-while은 한번은 무조건 실행을 하고 그후에 조건에 따라 실행유무 판단
	}

}
